import { Fragment, useCallback, useEffect, useMemo, useState } from "react";
import { InputSelect } from "./components/InputSelect";
import { Instructions } from "./components/Instructions";
import { Transactions } from "./components/Transactions";
import { useEmployees } from "./hooks/useEmployees";
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions";
import { useTransactionsByEmployee } from "./hooks/useTransactionsByEmployee";
import { EMPTY_EMPLOYEE } from "./utils/constants";
import { Employee, Transaction } from "./utils/types";

export function App() {
  const { data: employees, ...employeeUtils } = useEmployees();
  const {
    data: paginatedTransactions,
    loading: paginatedTransactionsLoading,
    fetchAll: fetchPaginatedTransactions,
    invalidateData: invalidatePaginatedTransactions,
  } = usePaginatedTransactions();
  const { data: transactionsByEmployee, ...transactionsByEmployeeUtils } = useTransactionsByEmployee();
  const [isLoading, setIsLoading] = useState(false);
  const [filteredTransactions, setFilteredTransactions] = useState<null | Transaction[]>(null); /* bug 6 */

  const loadAllTransactions = useCallback(async () => {
    setIsLoading(true);
    transactionsByEmployeeUtils.invalidateData();

    await employeeUtils.fetchAll();
    await fetchPaginatedTransactions();

    setIsLoading(false);
  }, [employeeUtils, fetchPaginatedTransactions, transactionsByEmployeeUtils]);

  const loadTransactionsByEmployee = useCallback(
    async (employeeId: string) => {
      invalidatePaginatedTransactions();
      await transactionsByEmployeeUtils.fetchById(employeeId);

      // Update filtered transactions state
      setFilteredTransactions(transactionsByEmployee);
    },
    [invalidatePaginatedTransactions, transactionsByEmployeeUtils]
  );

  useEffect(() => {
    if (employees === null && !employeeUtils.loading) {
      loadAllTransactions();
    }
  }, [employeeUtils.loading, employees, loadAllTransactions]);

  // Determine the transactions to be displayed based on the selected filter
  const transactions = useMemo(() => {
    if (filteredTransactions !== null) {
      return filteredTransactions;
    }
    return paginatedTransactions?.data ?? null;
  }, [filteredTransactions, paginatedTransactions]);

  // Determine if there are more transactions to fetch
  const hasMoreTransactions = useMemo(() => {
    return paginatedTransactions?.nextPage !== null;
  }, [paginatedTransactions]);

  return (
    <Fragment>
      <main className="MainContainer">
        <Instructions />

        <hr className="RampBreak--l" />

        <InputSelect<Employee>
          isLoading={isLoading}
          defaultValue={EMPTY_EMPLOYEE}
          items={employees === null ? [] : [EMPTY_EMPLOYEE, ...employees]}
          label="Filter by employee"
          loadingLabel="Loading employees"
          parseItem={(item) => ({
            value: item.id,
            label: `${item.firstName} ${item.lastName}`,
          })}
          onChange={async (newValue) => {
            if (newValue === null) {
              // Clear the filtered transactions when "All Employees" is selected
              setFilteredTransactions(null);
              return;
            }

            // Load transactions for the selected employee
            await loadTransactionsByEmployee(newValue.id);
          }}
        />

        <div className="RampBreak--l" />

        <div className="RampGrid">
          <Transactions transactions={transactions} />

          {transactions !== null && filteredTransactions === null && hasMoreTransactions && ( // Display View More only for paginated transactions with more data
            <button
              className="RampButton"
              disabled={paginatedTransactionsLoading}
              onClick={async () => {
                await fetchPaginatedTransactions();
              }}
            >
              View More
            </button>
          )}
        </div>
      </main>
    </Fragment>
  );
}
